import React, { useState } from 'react';
import './App.css';

function App() {
  const [assignament, setAssignament] = useState('');

  const addAssignamet = ({ target }) => {
    setAssignament(target.value);
  }

  return (
    <div className="App">
      <h2>Minha Tarefa</h2>
      <label>
        Digite Tarefa:
        <input type="text" value={ assignament } onChange={ addAssignamet }/>
      </label>
      <button type='button'>Adicionar</button>
    </div>
  );
}

export default App;
