import React, { useState } from 'react';
import './App.css';
import useAddAssingnament from './useAddAssignament';

function App() {
  const [assignament, setAssignament] = useState('');

  const addAssignamet = ({ target }) => {
    setAssignament(target.value);
  }

  const [newAssingnamet, setAddAssingnamet] = useAddAssingnament();

  return (
    <div className="App">
      <h2>Minha Tarefa</h2>
      <label>
        Digite Tarefa:
        <input type="text" onChange={ addAssignamet }/>
      </label>
      <button type='button' onClick={ () => setAddAssingnamet() }>Adicionar</button>
      <ul>
        {
          newAssingnamet.map((assig) => (
            <li key={ assig }>{assig}</li>
          ))
        }
      </ul>
    </div>
  );
}

export default App;
