import { useEffect, useState } from "react";

function useAddAssignament(assignament) {
  const [myAssingnamet, setMyAssingnamet] = useState([]);

  useEffect(() => {
    setMyAssingnamet(assignament);
  }, [myAssingnamet]);

  return [myAssingnamet, setMyAssingnamet];
}

export default useAddAssignament;
