import { useEffect, useState } from "react";

function useAddAssignament() {
  const [myAssingnamet, setMyAssingnamet] = useState('');
  const [newAssignament, setAssignament] = useState([]);

  useEffect(() => {
    setAssignament(myAssingnamet)
  }, [myAssingnamet]);

  return [newAssignament, setMyAssingnamet];
}

export default useAddAssignament;
