import { useEffect, useState } from "react";

function useAddAssignament() {
  const [addAssingnamet, setAddAssingnamet] = useState('');
  const [newAssignament, setNewAssignament] = useState([]);

  useEffect(() => {
    setNewAssignament(addAssingnamet.concat(addAssingnamet))
  }, [addAssingnamet]);

  return [newAssignament, setAddAssingnamet];
}

export default useAddAssignament;
